mapper.select:
SELECT 

FROM advertisement_position a

WHERE a.id = #{id}

SELECT 

FROM advertisement_position a

WHERE a.name = #{name}

SELECT
FROM advertisement_position a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM advertisement_position a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE advertisement_position SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
