mapper.select:
SELECT 

FROM order_table_item a

WHERE a.id = #{id}

SELECT 

FROM order_table_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_table_id = #{orderTable.id}


		AND a.is_gift = #{isGift}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY #{page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM order_table_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY #{page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE order_table_item SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE order_table_id = #{orderTable.id}

mapper.select:
select 
,p.wx_is_marketable AS "wxIsMarketable", p.stock- p.allocated_stock AS stock
from order_table_item a  left join product p on a.goods_no = p.id  where order_no=#{orderNo}
