mapper.select:
SELECT

FROM product_specification a

WHERE a.id = #{id}

SELECT

FROM product_specification a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE
		'%'||#{name}||'%'
		'%'+#{name}+'%'
		concat('%',#{name},'%')


		AND a.merchant_no = #{merchantNo}


		AND a.specification_group_id = #{SpecificationGroup.id}


		AND a.remarks = #{remarks}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT

FROM product_specification a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE product_specification SET
del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE specification_group_id = #{SpecificationGroup.id}

mapper.select:
SELECT g.id AS group_id,g.content_type AS content_type,s.id AS specification_id,s.name AS specification_name FROM specification_group g RIGHT JOIN product_specification s ON g.id=s.specification_group_id
WHERE g.id IN
#{id}

SELECT

FROM product_specification a LEFT JOIN product_specification_ref p ON a.id=p.specification_id

	p.product_id=#{productId}
